import React, { Component } from 'react';
import './ProfileModal.js';

export default class ProfileModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      TaiKhoan: '',
      MatKhau: '',
      HoTen: '',
      Email: '',
      SoDT: '',
      MaLoaiNguoiDung: 'HV'
    }
  }
  componentWillReceiveProps = nextProps => {
      this.setState()
  }
  onSubmit = e => {

  }
  handleOnChange = e => {
    var { name, value } = e.target;
    this.setState({
      [name] : value
    },()=> console.log(this.state))

  }
  render() {
    var { userProfile } = this.props;
    console.log(userProfile);


    return (
      <div className="modal fade" id="editprofile">
        <div className="modal-dialog  bg-modal ">
          <div className="modal-content">
            {/* Modal Header */}
            <div className="modal-header">
              <h4 className="modal-title" style={{ fontSize: '25px', fontWeight: '700' }}>Edit Profile</h4>
              <button type="button" className="close" data-dismiss="modal">×</button>
            </div>
            {/* Modal body */}
            <div className="modal-body">
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  <label>User</label>
                  <input
                    type="text"
                    className="form-control"
                    value={userProfile.TaiKhoan}
                    disabled
                  />
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    className="form-control"
                    value={userProfile.Email}
                    name="Email"
                    onChange={this.handleOnChange}
                  />
                </div>
                <div className="form-group">
                  <label>Full name</label>
                  <input
                    type="text"
                    className="form-control"
                    value={userProfile.HoTen}
                    name="HoTen"
                    onChange={this.handleOnChange}
                  />
                </div>
                <div className="form-group">
                  <label >Phone</label>
                  <input
                    type="text"
                    className="form-control"
                    value={userProfile.SoDT}
                    name="SoDT"
                    onChange={this.handleOnChange}
                  />
                </div>
                <div className="form-group">
                  <label >Password</label>
                  <input
                    type="password"
                    className="form-control"
                    value={userProfile.MatKhau}
                    name="MatKhau"
                    onChange={this.handleOnChange}
                  />
                </div>

              </form>
            </div>
            {/* Modal footer */}
            <div className="modal-footer">
              <button type="submit" className="btn btn-primary">Change</button>
              <button type="button" className="btn btn-danger" data-dismiss="modal">Close</button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
