import React, { Component } from 'react'
import { connect } from 'react-redux';
import { actDeleteUserRequest } from '../../store/action/user.action';

class UserItem extends Component {
    onDeleteUser = (taiKhoan) => {
        this.props.deleteUser(taiKhoan)
    }
    render() {
        var { user, index } = this.props

        return (
            <tr>
                <td>{index}</td>
                <td>{user.TaiKhoan}</td>
                <td>{user.Email}</td>
                <td>{user.HoTen}</td>
                <td>{user.SoDT}</td>
                <td>{user.MaLoaiNguoiDung}</td>
                <td className="text-center">
                    <i className="far fa-trash-alt mr-2" onClick={ this.onDeleteUser.bind(this, su)}></i>
                    <i className="far fa-edit"></i>
                </td>
            </tr>
        )
    }
}
const mapStateToProps = state => {
    return {
        // userList : state.user
    }
}
const mapDispatchToProps = dispatch => {
    return {
        deleteUser : (taiKhoan) => {
            dispatch(actDeleteUserRequest(taiKhoan));
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserItem);
