import React, { Component } from 'react';
import {connect} from 'react-redux';
import { actAddUserRequest } from '../../store/action/user.action';

 class ModalUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            TaiKhoan: '',
            MatKhau: '',
            HoTen: '',
            Email: '',
            SoDT: '',
            MaLoaiNguoiDung: 'HV',
            formErrors: {
                HoTen: '',
                Email: '',
                SoDT: '',
                TaiKhoan: '',
                MatKhau: ''
            }
        }
    }
    onSubmit = e => {
        e.preventDefault();
        var { TaiKhoan, HoTen, Email, SoDT, MatKhau, MaLoaiNguoiDung } = this.state
        var user = {
            TaiKhoan: TaiKhoan,
            MatKhau: MatKhau,
            HoTen: HoTen,
            Email: Email,
            SoDT: SoDT,
            MaLoaiNguoiDung : MaLoaiNguoiDung
        }
        this.props.addUser(user);
    }
    handleOnChange = e => {
        let name = e.target;
        let value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        this.setState({
            [name] : value
        })
    }
    render() {
        var { TaiKhoan, HoTen, Email, SoDT, MatKhau, formErrors, MaLoaiNguoiDung } = this.state

        return (
            <div className="modal fade" id="edit_user">
                <div className="modal-dialog  bg-modal ">
                    <div className="modal-content">
                        <form onSubmit={this.onSubmit}>
                            {/* Modal Header */}
                            <div className="modal-header">
                                <h4 className="modal-title" style={{ fontSize: '25px', fontWeight: '700' }}>Add User</h4>
                                <button type="button" className="close" data-dismiss="modal">×</button>
                            </div>
                            {/* Modal body */}
                            <div className="modal-body">
                                <div className="form-group">
                                    <label>User</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={TaiKhoan}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="email"
                                        className="form-control"
                                        value={Email}
                                        name="Email"
                                        onChange={this.handleOnChange}
                                    />
                                    {formErrors.Email.length > 0 && (
                                        <span className="errorMessage" style={{ marginLeft: '17%' }}>{formErrors.Email}</span>
                                    )}
                                </div>
                                <div className="form-group">
                                    <label>Full name</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={HoTen}
                                        name="HoTen"
                                        onChange={this.handleOnChange}
                                    />
                                    {formErrors.HoTen.length > 0 && (
                                        <span className="errorMessage" style={{ marginLeft: '17%' }}>{formErrors.HoTen}</span>
                                    )}
                                </div>
                                <div className="form-group">
                                    <label >Phone</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={SoDT}
                                        name="SoDT"
                                        onChange={this.handleOnChange}
                                    />
                                    {formErrors.SoDT.length > 0 && (
                                        <span className="errorMessage" style={{ marginLeft: '17%' }}>{formErrors.SoDT}</span>
                                    )}
                                </div>
                                <div className="form-group">
                                    <label >Password</label>
                                    <input
                                        type="password"
                                        className="form-control input-placeholder"
                                        value={MatKhau}
                                        name="MatKhau"
                                        onChange={this.handleOnChange}
                                    />
                                    {formErrors.MatKhau.length > 0 && (
                                        <span className="errorMessage" style={{ marginLeft: '17%' }}>{formErrors.MatKhau}</span>
                                    )}
                                </div>
                                <div className="form-group">
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input 
                                                type="checkbox" 
                                                className="form-check-input" 
                                                defaultValue 
                                                onChange={this.handleOnChange}
                                                name="MaLoaiNguoiDung"
                                                value={MaLoaiNguoiDung}
                                                checked
                                                />Học viên
                                        </label>
                                    </div>
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input 
                                                type="checkbox" 
                                                className="form-check-input" 
                                                defaultValue 
                                                onChange={this.handleOnChange}
                                                name="MaLoaiNguoiDung"
                                                value={MaLoaiNguoiDung}
                                                />Giáo vụ
                                        </label>
                                    </div>

                                </div>

                            </div>
                            {/* Modal footer */}
                            <div className="modal-footer">
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    // disabled={
                                    //     formErrors.HoTen !== '' ||
                                    //     formErrors.Email !== '' ||
                                    //     formErrors.SoDT !== '' ||
                                    //     formErrors.MatKhau !== '' ||
                                    //     HoTen === '' ||
                                    //     Email === '' ||
                                    //     SoDT === '' ||
                                    //     MatKhau === ''
                                    // }
                                >Add New</button>
                                <button type="button" ref="btnClose" className="btn btn-danger" data-dismiss="modal">Close</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => {
    return {
        addUser : (user) => {
            dispatch(actAddUserRequest(user));
        }
    }
}
export default connect(null, mapDispatchToProps)(ModalUser)
