import React, { Component } from 'react'
import { connect } from 'react-redux';
import { actDeleteUserRequest, actGetUserEditing, actUpdateUserRequest,  } from '../../store/action/user.action';

class UserItem extends Component {
    onDeleteUser = (taiKhoan) => {
        this.props.deleteUser(taiKhoan)
    }
    onUpdateUser = () => {
        this.props.userEditing(this.props.user);
        this.props.userUpdate(this.props.getUserEditing)
        
    }
    render() {
        var { user, index } = this.props

        return (
            <tr>
                <td>{index}</td>
                <td>{user.TaiKhoan}</td>
                <td>{user.Email}</td>
                <td>{user.HoTen}</td>
                <td>{user.SoDT}</td>
                <td>{user.MaLoaiNguoiDung}</td>
                <td className="text-center">
                    <i className="far fa-trash-alt mr-2"
                        onClick={this.onDeleteUser.bind(this, user.TaiKhoan)}
                        style={{ cursor: 'pointer' }}>
                    </i>
                    <i  className="far fa-edit"
                        style={{ cursor: 'pointer' }} 
                        data-toggle="modal"
                        data-target="#action_user"
                        onClick={this.onUpdateUser}>
                    </i>
                </td>
            </tr>
        )
    }
}
const mapStateToProps = state => {
    console.log(state.userEditing);
    
    return {
        getUserEditing : state.userEditing
    }
}
const mapDispatchToProps = dispatch => {
    return {
        deleteUser: (taiKhoan) => {
            dispatch(actDeleteUserRequest(taiKhoan));
        },
        userEditing : (userEditing) => {
            dispatch(actGetUserEditing(userEditing));
        },
        userUpdate : (user) => {
            dispatch(actUpdateUserRequest(user));
        }


    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserItem);
