import React, { Component } from 'react';
import {connect} from 'react-redux';
import { actAddUserRequest, actGetAllUsersRequest } from '../../store/action/user.action';

 class UserContainer extends Component {
    render() {
        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <button className="btn btn-primary" data-toggle="modal" data-target="#edit_user">Add User</button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            <table className="table table-bordered" id="dataTable" width="100%" cellSpacing={0}>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Position</th>
                                        <th>Office</th>
                                        <th>Age</th>
                                        <th>Start date</th>
                                        <th>Salary</th>
                                    </tr>
                                </thead>
                                <tbody>

                                    <tr>
                                        <td>Donna Snider</td>
                                        <td>Customer Support</td>
                                        <td>New York</td>
                                        <td>27</td>
                                        <td>2011/01/25</td>
                                        <td>$112,000</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    return {
        user : state.user
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser : () => {
            dispatch(actGetAllUsersRequest());
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
