import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actGetAllUsersRequest, actChangeEditStatus } from '../../store/action/user.action';
import UserItem from '../../components/Admin/UserItem';
import './User.css'
import { actSearchUser, actSearchPosition } from '../../store/action/search.action';

class UserContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filterAcount : '',
            filterPosition : -1
        }
    }

    componentDidMount() {
        this.props.getAllUser();
    }
    onChangeStatusEdit = () => {
        this.props.changeStatusEdit();
    }
    handleOnChange = (e) => {
        let {name, value} =  e.target;
        let  filter = {
            account  : name === 'filterAcount' ? value : this.state.filterAcount,
            position : name === ''
        };
        // console.log(position);
        
        this.props.keyword(value.toLowerCase());
        this.props.searchByPosition(position);
        this.setState({
            [name]: value
        })
    }
    render() {
        var {filterAcount, filterPosition} = this.state;
        var { searchUser, usersList } = this.props;
        var userElm = null;
        var displayUser = [];
        if(searchUser){
            displayUser = usersList.filter(user => user.TaiKhoan.indexOf(searchUser) !== -1);
        }
        // filter position
        // displayUser = usersList.filter( (user) => {
        //     if(this.state.filterPosition === -1){
        //         return user;
        //     }else{
        //         // return user.MaLoaiNguoiDung === ();
        //     }
        // })
        
        userElm = displayUser.map((user, index) => {
            return <UserItem
                key={index}
                index={index + 1}
                user={user}
            />
        })
        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <button
                        className="btn btn-primary"
                        data-toggle="modal" data-target="#action_user"
                        onClick={this.onChangeStatusEdit}
                    >
                        Add User
                    </button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            <div className="col-12">
                                <table className="table table-bordered" id="dataTable" width="100%" cellSpacing={0} style={{ fontSize: '13px' }}>
                                    <thead>
                                        <tr className="text-center" style={{ color: 'black' }}>
                                            <th>Index</th>
                                            <th>User</th>
                                            <th>Email</th>
                                            <th>Name</th>
                                            <th>Phone</th>
                                            <th>Position</th>
                                            <th>Action</th>
                                        </tr>
                                        <tr>
                                            <td></td>
                                            <td>
                                                {/* <!-- Search form --> */}
                                                <div className="md-form active-cyan-2 mb-3">
                                                    <input
                                                        className="form-control search-user"
                                                        type="text" placeholder="Search ..." aria-label="Search"
                                                        name="filterAccount"
                                                        onChange={this.handleOnChange}
                                                        value={filterAcount}
                                                    />
                                                </div>
                                            </td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td>
                                                <select
                                                    className="form-control"
                                                    name="filterPosition"
                                                    onChange={this.handleOnChange}
                                                    value={filterPosition}
                                                >
                                                    <option value="-1">Tất Cả</option>
                                                    <option value="0">Học viên</option>
                                                    <option value="1">Giáo vụ</option>
                                                </select>
                                            </td>
                                            <td></td>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {userElm}
                                    </tbody>
                                </table>

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    console.log(state);

    return {
        usersList: state.user,
        userEditing: state.userEditing,
        searchUser: state.searchUser
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser: () => {
            dispatch(actGetAllUsersRequest());
        },
        changeStatusEdit: () => {
            dispatch(actChangeEditStatus());
        },
        keyword: (keyword) => {
            dispatch(actSearchUser(keyword));
        },
        // searchByPosition : (position) => {
        //     dispatch(actSearchPosition(position));
        // }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
