import React, { Component, Fragment } from 'react';
import {Redirect} from 'react-router-dom';
import Header from './../components/Home/Header';
import Swal from 'sweetalert2'


export default class LayoutHome extends Component {
    
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                <Header />
                {Component}
            </Fragment>
        )
    }
};

export class LayoutAdmin extends Component {
    render() {
        const Component = this.props.children;
        const data = JSON.parse(sessionStorage.getItem('UserLogin'));
        if(data && data.MaLoaiNguoiDung === "HV"){
            return <Redirect to="/" />
        }
        if(data && data.MaLoaiNguoiDung === "GV"){
            return <Redirect to="/admin" />
        }
        if(!data){
            const Toast = Swal.mixin({
                toast: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000
              });
              
              Toast.fire({
                type: 'w',
                title: 'Signed in successfully'
              })
            return <Redirect to="/login" />
        }
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
export class LayoutLogin extends Component {
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
export class LayoutRegister extends Component {
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
