import * as Types from '../constants/ActionTypes';
import CourseService from './../../services/course.services';
import Swal from 'sweetalert2';

const courseService = new CourseService();

export const actGetCourses = (courseList) => {
    return {
        type : Types.GET_COURSES,
        courseList
    }
}
export const actGetCoursesRequest = () => {
    return dispatch => {
        return courseService.getCourses()
            .then( res => {
                // console.log(res);
                
                dispatch(actGetCourses(res.data))
            })
            .catch( err => {
                console.log(err);
            })
    }
}

export const actAddCourse = course => {
    return {
        type : Types.ADD_COURSE,
        course
    }
}
export const actAddCourseRequest = course => {
    return dispatch => {
        return courseService.addCourse(course)
        .then(res => {
            console.log(res);
            dispatch(actAddCourse(course));
        })
        .catch(err => {
            console.log(err);    
        })
    }
}
export const actDeleteCourse = id => {
    return {
        type : Types.ADD_COURSE,
        id
    }
}
export const actDeleteCourseRequest = id => {
    return dispatch => {
        Swal.fire({
            title: 'Are you sure?',
            type: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!',
        }).then((result) => {
            if (result.value) {
                //click ok
                return courseService.deleteCourse(id)
                    .then(res => {
                        dispatch(actDeleteCourse(id));
                        Swal.fire(
                            'Deleted!',
                            'User select has been deleted.',
                            'success'
                        )
                    })
                    .catch(err => {
                        console.log(err);

                    })
            } else {
                //click cancel
                Swal.fire({
                    type: 'error',
                    title: 'Fail...',
                    text: 'Deleted Fail',
                })
            }
        })
    }
}
