import React, { Component, Fragment } from 'react';
import {Redirect} from 'react-router-dom';
import Header from './../components/Home/Header';
import Swal from 'sweetalert2'


export default class LayoutHome extends Component {
    
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                <Header />
                {Component}
            </Fragment>
        )
    }
};

export class LayoutAdmin extends Component {
    render() {
        const Component = this.props.children;
        const data = JSON.parse(sessionStorage.getItem('UserLogin'));
        if(data && data.MaLoaiNguoiDung === "HV"){
            return <Redirect to="/" />
        }
        if(data && data.MaLoaiNguoiDung === "GV"){
            return <Redirect to="/admin" />
        }
        if(!data){
            // Swal.fire({
            //     position: 'center',
            //     type: 'warning',
            //     title: 'Please login !!',
            //     showConfirmButton: true,
            //     timer: 1500
            // }).then( (willDelete) => {
            //     if(willDelete){
            //         return <Redirect to="/login" />
            //     }
            // })
            swal({
                title: "Are you sure?",
                text: "Once deleted, you will not be able to recover this imaginary file!",
                icon: "warning",
                buttons: true,
                dangerMode: true,
              })
              .then((willDelete) => {
                if (willDelete) {
                  swal("Poof! Your imaginary file has been deleted!", {
                    icon: "success",
                  });
                } else {
                  swal("Your imaginary file is safe!");
                }
              });
        }
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
export class LayoutLogin extends Component {
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
export class LayoutRegister extends Component {
    render() {
        const Component = this.props.children;
        return (
            <Fragment>
                {Component}
            </Fragment>
        )
    }
};
