import * as Types from './../constants/ActionTypes';

let initialState = {};
const UserReducer = (state = initialState, action) => {
    switch(action.type){
        case Types.ADD_USER : 
            state = action.user
            return {...state};
        default : return state;
    }
}
export default UserReducer;
