import React, { Component, Fragment } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import {actRegisterUserRequest} from './../../store/action/auth.action';



class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            HoTen: '',
            Email: '',
            SoDT: '',
            TaiKhoan: '',
            MatKhau: '',
            MaLoaiNguoiDung: 'HV',

        }
    }
    handleOnChange = e => {
        var name = e.target.name;
        var value = e.target.checkbox === 'checkbox' ? e.target.checked : e.target.value ;
        this.setState({
            [name] : value
        });
    }
    onSubmit = e => {
        let {HoTen, Email, SoDT, TaiKhoan, MatKhau, isRegister} = this.state;
        let user = {
            HoTen : HoTen,
            Email : Email,
            SoDT : SoDT,
            TaiKhoan : TaiKhoan,
            MatKhau : MatKhau,
            MaLoaiNguoiDung : this.state.MaLoaiNguoiDung,
            isRegister : !isRegister
        }
        e.preventDefault();
        this.props.registerUser(user);
        log
    }
    render() {
        var {HoTen, Email, SoDT, TaiKhoan, MatKhau} = this.state;
        return (
            <Fragment>
                <h1 className="display-5 mt-5 text-center">Register</h1>
                <div className="container">
                    <div className="row">
                        <div className="col-6 m-auto" >
                            <form onSubmit={this.onSubmit} ref={c => { this.form = c }}>
                                <div className="form-group">
                                    <label>Name</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter your name"
                                        name="HoTen"
                                        value={HoTen}
                                        onChange={this.handleOnChange}
                                    />

                                </div>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter your email"
                                        name="Email"
                                        value={Email}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Phone</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        placeholder="Enter your phone"
                                        name="SoDT"
                                        value={SoDT}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>User</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter your user"
                                        name="TaiKhoan"
                                        value={TaiKhoan}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Enter your password"
                                        name="MatKhau"
                                        value={MatKhau}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                {/* <div className="form-group">
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input
                                                type="checkbox"
                                                className="form-check-input"
                                                value={MaLoaiNguoiDung}
                                                name="MaLoaiNguoiDung"
                                                onChange={this.handleOnChange}
                                            />
                                            Học viên
                                        </label>
                                    </div>
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input
                                                type="checkbox"
                                                className="form-check-input"
                                                value={MaLoaiNguoiDung}
                                                name="MaLoaiNguoiDung"
                                                onChange={this.handleOnChange}
                                            />
                                            Giáo vụ
                                        </label>
                                    </div>
                                </div> */}
                                <button type="submit" className="btn btn-outline-primary mr-2">Register</button>
                                <Link to="/">
                                    <button type="submit" className="btn btn-outline-danger">Cancel</button>
                                </Link>
                                <Link to="/login"><span className="float-right">I'm ready member</span></Link>
                            </form>
                        </div>
                    </div>
                </div>
            </Fragment>

        )
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        registerUser : user => {
            dispatch(actRegisterUserRequest(user));
        }
    }
}
export default connect(null, mapDispatchToProps)(Register);
