import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
// Validation
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import CheckButton from 'react-validation/build/button';
import { isEmail, isEmpty } from 'validator';

const required = value => {
    if (isEmpty(value)) {
        return <small className="form-text text-danger">This field is required</small>
    }
}
const email = (value) => {
    if (!isEmail(value)) {
        return <small className="form-text text-danger">Invalid email format</small>;
    }
}

const minLength = (value) => {
    if (value.trim().length < 6) {
        return <small className="form-text text-danger">Password must be at least 6 characters long</small>;
    }
}

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            HoTen: '',
            Email: '',
            SoDT: '',
            TaiKhoan: '',
            MatKhau: '',
            MaLoaiNguoiDung: '',

        }
    }
    handleOnChange = (e) => {
        var name = e.target.name;
        var value = e.target.checkbox === 'checkbox' ? e.target.checked : e.target.value ;
        this.setState({
            [name] : value
        });
    }
    onSubmit = e => {
        e.preventDefault();
        // console.log(this.state);
        
    }
    render() {
        var {HoTen, Email, SoDT, TaiKhoan, MaLoaiNguoiDung, MatKhau} = this.state;
        return (
            <Fragment>
                <h1 className="display-5 mt-5 text-center">Register</h1>
                <div className="container">
                    <div className="row">
                        <div className="col-6 m-auto" >
                            <form onSubmit={this.onSubmit} ref={c => { this.form = c }}>
                                <div className="form-group">
                                    <label>Name</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="HoTen"
                                        value={HoTen}
                                        onChange={this.handleOnChange}
                                    />

                                </div>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="Email"
                                        value={Email}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Phone</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        name="SoDT"
                                        value={SoDT}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>User</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="TaiKhoan"
                                        value={TaiKhoan}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        name="MatKhau"
                                        value={MatKhau}
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input
                                                type="checkbox"
                                                className="form-check-input"
                                                value={MaLoaiNguoiDung}
                                                name="MaLoaiNguoiDung"
                                                onChange={this.handleOnChange}
                                            />
                                            Học viên
                                </label>
                                    </div>
                                    <div className="form-check-inline">
                                        <label className="form-check-label">
                                            <input
                                                type="checkbox"
                                                className="form-check-input"
                                                value={MaLoaiNguoiDung}
                                                name="MaLoaiNguoiDung"
                                                onChange={this.handleOnChange}
                                            />
                                            Giáo vụ
                                </label>
                                    </div>
                                </div>
                                <div className="form-group">
                                    <button type="submit" className="btn btn-success mr-2">Register</button>
                                    {/* <CheckButton style={{ display: 'none' }} ref={c => { this.checkBtn = c }} /> */}
                                    <button type="submit" className="btn btn-danger">Cancel</button>
                                    <Link to="/login"><span className="float-right">Login</span></Link>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </Fragment>

        )
    }
}
export default Register;
