import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';

export default class Sidebar extends Component {
    render() {
        return (
            <Fragment>
                <header className="topbar" data-navbarbg="skin6">
                    <nav className="navbar top-navbar navbar-expand-md navbar-light">
                        <div className="navbar-header" data-logobg="skin5">
                            {/* This is for the sidebar toggle which is visible on mobile only */}
                            <a className="nav-toggler waves-effect waves-light d-block d-md-none" href="javascript:void(0)">
                                <i className="ti-menu ti-close" />
                            </a>
                            {/* ============================================================== */}
                            {/* Logo */}
                            {/* ============================================================== */}
                            <div className="navbar-brand">
                                <a href="index.html" className="logo">
                                    {/* Logo icon */}
                                    <b className="logo-icon">
                                        {/*You can put here icon as well // <i class="wi wi-sunset"></i> //*/}
                                        {/* Dark Logo icon */}
                                        <img src="../../assets/images/logo-icon.png" alt="homepage" className="dark-logo" />
                                        {/* Light Logo icon */}
                                        <img src="../../assets/images/logo-light-icon.png" alt="homepage" className="light-logo" />
                                    </b>
                                    {/*End Logo icon */}
                                    {/* Logo text */}
                                    <span className="logo-text">
                                        {/* dark Logo text */}
                                        <img src="../../assets/images/logo-text.png" alt="homepage" className="dark-logo" />
                                        {/* Light Logo text */}
                                        <img src="../../assets/images/logo-light-text.png" className="light-logo" alt="homepage" />
                                    </span>
                                </a>
                            </div>
                            {/* ============================================================== */}
                            {/* End Logo */}
                            {/* ============================================================== */}
                            {/* ============================================================== */}
                            {/* Toggle which is visible on mobile only */}
                            {/* ============================================================== */}
                            <a className="topbartoggler d-block d-md-none waves-effect waves-light" href="javascript:void(0)" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                                <i className="ti-more" />
                            </a>
                        </div>
                        {/* ============================================================== */}
                        {/* End Logo */}
                        {/* ============================================================== */}
                        <div className="navbar-collapse collapse" id="navbarSupportedContent" data-navbarbg="skin6">
                            {/* ============================================================== */}
                            {/* toggle and nav items */}
                            {/* ============================================================== */}
                            <ul className="navbar-nav float-left mr-auto">
                                {/* ============================================================== */}
                                {/* Search */}
                                {/* ============================================================== */}
                                <li className="nav-item search-box">
                                    <a className="nav-link waves-effect waves-dark" href="javascript:void(0)">
                                        <div className="d-flex align-items-center">
                                            <i className="mdi mdi-magnify font-20 mr-1" />
                                            <div className="ml-1 d-none d-sm-block">
                                                <span>Search</span>
                                            </div>
                                        </div>
                                    </a>
                                    <form className="app-search position-absolute">
                                        <input type="text" className="form-control" placeholder="Search & enter" />
                                        <a className="srh-btn">
                                            <i className="ti-close" />
                                        </a>
                                    </form>
                                </li>
                            </ul>
                            {/* ============================================================== */}
                            {/* Right side toggle and nav items */}
                            {/* ============================================================== */}
                            <ul className="navbar-nav float-right">
                                {/* ============================================================== */}
                                {/* User profile and search */}
                                {/* ============================================================== */}
                                <li className="nav-item dropdown">
                                    <a className="nav-link dropdown-toggle text-muted waves-effect waves-dark pro-pic" href data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"><img src="../../assets/images/users/1.jpg" alt="user" className="rounded-circle" width={31} /></a>
                                    <div className="dropdown-menu dropdown-menu-right user-dd animated">
                                        <a className="dropdown-item" href="javascript:void(0)"><i className="ti-user m-r-5 m-l-5" /> My Profile</a>
                                        <a className="dropdown-item" href="javascript:void(0)"><i className="ti-wallet m-r-5 m-l-5" /> My Balance</a>
                                        <a className="dropdown-item" href="javascript:void(0)"><i className="ti-email m-r-5 m-l-5" /> Inbox</a>
                                    </div>
                                </li>
                                {/* ============================================================== */}
                                {/* User profile and search */}
                                {/* ============================================================== */}
                            </ul>
                        </div>
                    </nav>
                </header>

                <aside className="left-sidebar" data-sidebarbg="skin5" style={{backgroundCp: '#23324'}}>
                    {/* Sidebar scroll*/}
                    <div className="scroll-sidebar">
                        {/* Sidebar navigation*/}
                        <nav className="sidebar-nav">
                            <ul id="sidebarnav">
                                <li className="sidebar-item">
                                    <a className="sidebar-link waves-effect waves-dark sidebar-link" href="index.html" aria-expanded="false">
                                        <i className="mdi mdi-av-timer" />
                                        <span className="hide-menu">Dashboard</span>
                                    </a>
                                </li>
                                <li className="sidebar-item">
                                    <Link to="/admin/profile" className="sidebar-link waves-effect waves-dark sidebar-link" aria-expanded="false">
                                        <i className="mdi mdi-account-network" />
                                        <span className="hide-menu">Profile</span>
                                    </Link>
                                </li>
                                <li className="sidebar-item">
                                    <Link to="/admin/courses" className="sidebar-link waves-effect waves-dark sidebar-link" aria-expanded="false">
                                        <i className="mdi mdi-arrange-bring-forward" />
                                        <span className="hide-menu">Courses</span>
                                    </Link>
                                </li>
                                <li className="sidebar-item">
                                    <Link to="/admin/users" className="sidebar-link waves-effect waves-dark sidebar-link" aria-expanded="false">
                                        <i className="mdi mdi-border-none" />
                                        <span className="hide-menu">Users</span>
                                    </Link>
                                </li>


                                {/* <li className="sidebar-item">
                            <a className="sidebar-link waves-effect waves-dark sidebar-link" href="error-404.html" aria-expanded="false">
                                <i className="mdi mdi-alert-outline" />
                                <span className="hide-menu">404</span>
                            </a>
                        </li> */}
                            </ul>
                        </nav>
                        {/* End Sidebar navigation */}
                    </div>
                    {/* End Sidebar scroll*/}
                </aside>

            </Fragment>
        )
    }
}
