import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actRegisterUserRequest } from './../../store/action/auth.action';
// import swal from 'sweetalert';
import Swal from 'sweetalert2';
import './Register.css';





class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            HoTen: '',
            Email: '',
            SoDT: '',
            TaiKhoan: '',
            MatKhau: '',
            MaLoaiNguoiDung: 'HV',

        }
    }
    handleOnChange = e => {
        var name = e.target.name;
        var value = e.target.checkbox === 'checkbox' ? e.target.checked : e.target.value;
        this.setState({
            [name]: value
        });
    }
    onSubmit = e => {
        let { HoTen, Email, SoDT, TaiKhoan, MatKhau, isRegister } = this.state;
        let user = {
            HoTen: HoTen,
            Email: Email,
            SoDT: SoDT,
            TaiKhoan: TaiKhoan,
            MatKhau: MatKhau,
            MaLoaiNguoiDung: this.state.MaLoaiNguoiDung,
            isRegister: !isRegister
        }
        e.preventDefault();
        this.props.registerUser(user);
        // swal("Done!", "Register successfully", "success");
        Swal.fire({
            position: 'center',
            type: 'success',
            title: 'Register successfully!',
            showConfirmButton: false,
            timer: 1500
        })
        this.props.history.replace("/login");

    }
    render() {
        var { HoTen, Email, SoDT, TaiKhoan, MatKhau } = this.state;
        return (
            <Fragment>
                <div className="container">
                    <div className="card bg-light">
                        <article className="card-body mx-auto" style={{maxWidth: '400px'}}>
                            <h4 className="card-title mt-3 text-center">Create Account</h4>
                            <p className="text-center">Get started with your free account</p>
                            <p>
                                <a href="" className="btn btn-block btn-twitter"> <i className="fa fa-twitter"></i>   Login via Twitter</a>
                                <a href="" className="btn btn-block btn-facebook"> <i className="fa fa-facebook"></i>   Login via facebook</a>
                            </p>
                            <p className="divider-text">
                                <span className="bg-light">OR</span>
                            </p>
                            <form>
                                <div className="form-group input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"> <i className="fa fa-user"></i> </span>
                                    </div>
                                    <input 
                                        name="HoTen"
                                        value={HoTen}
                                        onChange={this.handleOnChange}
                                        className="form-control" 
                                        placeholder="Full name" 
                                        type="text" />
                                </div>
                                <div className="form-group input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"> <i className="fa fa-envelope"></i> </span>
                                    </div>
                                    <input 
                                        name="" 
                                        className="form-control" 
                                        placeholder="Email address" 
                                        type="email" 
                                        name="HoTen"
                                        value={HoTen}
                                        onChange={this.handleOnChange}
                                        />
                                </div>
                                <div className="form-group input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"> <i className="fa fa-phone"></i> </span>
                                    </div>
                                    <input name="" className="form-control" placeholder="Phone number" type="number" />
                                </div>
                                <div className="form-group input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"> <i className="fa fa-user"></i> </span>
                                    </div>
                                    <input className="form-control" placeholder="User name" type="text" />
                                </div>
                                <div className="form-group input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"> <i className="fa fa-lock"></i> </span>
                                    </div>
                                    <input className="form-control" placeholder="Create password" type="password" />
                                </div>
                                
                                <div className="form-group">
                                    <button type="submit" className="btn btn-primary btn-block"> Create Account  </button>
                                </div>
                                <p className="text-center">Have an account? <Link to="/login">Log In</Link> </p>
                            </form>
                        </article>
                    </div>

                </div>

            </Fragment>

        )
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        registerUser: user => {
            dispatch(actRegisterUserRequest(user));
        }
    }
}
export default connect(null, mapDispatchToProps)(Register);
