import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actAddUserRequest, actGetAllUsersRequest, actChangeEditStatus } from '../../store/action/user.action';
import UserItem from '../../components/Admin/UserItem';

class UserContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            TaiKhoan: '',
            MatKhau: '',
            HoTen: '',
            Email: '',
            SoDT: '',
            MaLoaiNguoiDung: '',
        }
    }
    componentDidMount() {
        this.props.getAllUser();
    }
    onChangeStatusEdit = () => {
        this.props.changeStatusEdit();
    }
    componentWillReceiveProps(nextProps){
        console.log(nextProps);
        this
        
    }
    render() {
        let users = [];
        users = this.props.usersList.map((user, index) => {
            return <UserItem
                key={index}
                index={index + 1}
                user={user}
            />
        })
        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <button
                        className="btn btn-primary"
                        data-toggle="modal" data-target="#action_user"
                        onClick={this.onChangeStatusEdit}
                    >Add User</button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            <table className="table table-bordered" id="dataTable" width="100%" cellSpacing={0} style={{ fontSize: '12px' }}>
                                <thead>
                                    <tr className="text-center">
                                        <th>Index</th>
                                        <th>User</th>
                                        <th>Email</th>
                                        <th>Name</th>
                                        <th>Phone</th>
                                        <th>Position</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {users}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    console.log(state);
    
    return {
        usersList: state.user,
        userEditing : state.userEditing
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser: () => {
            dispatch(actGetAllUsersRequest());
        },
        changeStatusEdit : () => {
            dispatch(actChangeEditStatus());
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
