import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actGetAllUsersRequest, actChangeEditStatus } from '../../store/action/user.action';
import UserItem from '../../components/Admin/UserItem';
import './User.css'
import { actSearchUser } from '../../store/action/search.action';

class UserContainer extends Component {
    componentDidMount() {
        this.props.getAllUser();
    }
    onChangeStatusEdit = () => {
        this.props.changeStatusEdit();
    }
    handleOnChange = (e) => {
        this.props.keyword(e.target.value);
    }
    render() {
        let userElm = [];
        // let displayUserList = [];
        // let users =  this.props.usersList.filter(e=> e.HoTen && e.HoTen.indexOf(this.props.keyword) !== -1);
        if(keyword){
            displayUserList = this.props.userList.filter( (user) => {
                return user.TaiKhoan.indexOf(keyword) !== -1 ;            })
        }
        userElm = users.map((user, index) => {
            return <UserItem
                key={index}
                index={index + 1}
                user={user}
            />
        })
        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <div className="container h-100">
                        <div className="d-flex justify-content-center h-100">
                            <div className="searchbar">
                                <input 
                                    className="search_input" 
                                    type="text" 
                                    name="keyword" placeholder="Search..." 
                                    onChange={this.handleOnChange}
                                />
                                <a href="#" className="search_icon"><i className="fas fa-search"></i></a>
                            </div>
                        </div>
                    </div>
                    <button
                        className="btn btn-primary"
                        data-toggle="modal" data-target="#action_user"
                        onClick={this.onChangeStatusEdit}
                    >
                        Add User
                    </button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            <table className="table table-bordered" id="dataTable" width="100%" cellSpacing={0} style={{ fontSize: '12px' }}>
                                <thead>
                                    <tr className="text-center">
                                        <th>Index</th>
                                        <th>User</th>
                                        <th>Email</th>
                                        <th>Name</th>
                                        <th>Phone</th>
                                        <th>Position</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {userElm}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    console.log(state);
    
    return {
        usersList: state.user,
        userEditing: state.userEditing,
        searchUser : state.searchUser
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser: () => {
            dispatch(actGetAllUsersRequest());
        },
        changeStatusEdit: () => {
            dispatch(actChangeEditStatus());
        },
        keyword : (keyword) => {
            dispatch(actSearchUser(keyword));
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
