import React, { Component, Fragment } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from "react-redux";
import { actLoginUserRequest } from '../../store/action/auth.action';
// css
import './Login.css';

const formValid = ({ formErrors, ...rest }) => {
    let valid = true;

    // mẫu lỗi
    Object.values(formErrors).forEach(val => {
        val.length > 0 && (valid = false);
    });

    // Mẫu đã được điền
    Object.values(rest).forEach(val => {
        val === null && (valid = false);
    });

    return valid;
};


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            TaiKhoan: '',
            MatKhau: '',
            isLogined: false,
            formErrors: {
                TaiKhoan: '',
                MatKhau: ''
            },

        }
    }


    handleOnChange = e => {
        let { name, value } = e.target;
        let formErrors = { ...this.state.formErrors };
        // this.setState({
        //     [name]: value
        // });
        switch (name) {
            
            case "TaiKhoan":
                formErrors.TaiKhoan =
                    value.length < 3 ? "minimum 3 characaters required" : "";
                break;
            
            case "MatKhau":
                formErrors.password =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;
            default:
                break;
        }

        this.setState({ formErrors, [name]: value }, () => console.log(this.state));
    }
    onSubmit = e => {
        this.setState({
            isLogined: true
        });
        let user = {
            TaiKhoan: this.state.TaiKhoan,
            MatKhau: this.state.MatKhau,
            isLogined: true
        };
        e.preventDefault();
        this.props.onLogin(user);
        this.onClearForm();
    }

    onClearForm = () => {
        this.setState({
            TaiKhoan: '',
            MatKhau: '',
        })
    }

    render() {

        if (this.state.isLogined) {
            return <Redirect to="/" />
        }
        if (sessionStorage.getItem('UserLogin')) {
            return <Redirect to="/" />
        }
        var { TaiKhoan, MatKhau } = this.state;
        return (
            <Fragment>
                {/* Validation */}

                <section className="login-block">
                    <div className="container container-login">
                        <div className="row">
                            <div className="col-md-4 login-sec">
                                <h2 className="text-center">Login Now</h2>
                                <form className="login-form" onSubmit={this.onSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1" className="text-uppercase">Username</label>
                                        <input
                                            type="text"
                                            required
                                            className="form-control"
                                            name="TaiKhoan"
                                            value={TaiKhoan}
                                            onChange={this.handleOnChange}
                                        />

                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputPassword1" className="text-uppercase">Password</label>
                                        <input
                                            type="password"
                                            required
                                            className="form-control"
                                            name="MatKhau"
                                            value={MatKhau}
                                            onChange={this.handleOnChange}
                                        />
                                    </div>

                                    <div className="form-check">
                                        <label className="form-check-label">
                                            <input type="checkbox" className="form-check-input" />
                                            <small>Remember Me</small>
                                        </label>
                                        <button type="submit" className="btn btn-login float-right">Login</button>

                                    </div>
                                </form>
                            </div>
                            <div className="col-md-8 banner-sec">
                                <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
                                    <ol className="carousel-indicators">
                                        <li data-target="#carouselExampleIndicators" data-slide-to={0} className="active" />
                                        <li data-target="#carouselExampleIndicators" data-slide-to={1} />
                                        <li data-target="#carouselExampleIndicators" data-slide-to={2} />
                                    </ol>
                                    <div className="carousel-inner" role="listbox">
                                        <div className="carousel-item active">
                                            <img className="d-block img-fluid" src="https://static.pexels.com/photos/33972/pexels-photo.jpg" alt="First slide" />
                                            <div className="carousel-caption d-none d-md-block">
                                                <div className="banner-text">
                                                    <h2>This is Heaven</h2>
                                                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation</p>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="carousel-item">
                                            <img className="d-block img-fluid" src="https://images.pexels.com/photos/7097/people-coffee-tea-meeting.jpg" alt="First slide" />
                                            <div className="carousel-caption d-none d-md-block">
                                                <div className="banner-text">
                                                    <h2>This is Heaven</h2>
                                                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation</p>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="carousel-item">
                                            <img className="d-block img-fluid" src="https://images.pexels.com/photos/872957/pexels-photo-872957.jpeg" alt="First slide" />
                                            <div className="carousel-caption d-none d-md-block">
                                                <div className="banner-text">
                                                    <h2>This is Heaven</h2>
                                                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation</p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment>
        )
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        onLogin: user => {
            dispatch(actLoginUserRequest(user));
        }
    }
}
export default connect(null, mapDispatchToProps)(Login);
