import * as Types from './../constants/ActionTypes';
let initialState = {
    TaiKhoan : '',
    MatKhau : '',
    HoTen : '',
    Email : '',
    SoDT : '',
    MaLoaiNguoiDung : ''
}
const editUserReducer = (state = initialState, action) => {
    switch(action.type){
        case Types.UPDATE_USER : 
            state = action.userEditing
        return {...state};
        default: return state;
    }
}
export default editUserReducer;
