import React, { Component } from 'react';
import {connect} from 'react-redux';
import { actAddUserRequest, actGetAllUsersRequest } from '../../store/action/user.action';
import UserItem from '../../components/Admin/UserItem';

 class UserContainer extends Component {
     componentDidMount(){
         this.props.getAllUser();
     }
    render() {

        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <button className="btn btn-primary" data-toggle="modal" data-target="#edit_user">Add User</button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            <table className="table table-bordered" id="dataTable" width="100%" cellSpacing={0}>
                                <thead>
                                    <tr className="text-center">
                                        <th>User</th>
                                        <th>Email</th>
                                        <th>Name</th>
                                        <th>Phone</th>
                                        <th>Position</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <UserItem/>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    return {
        usersList : state.user
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser : () => {
            dispatch(actGetAllUsersRequest());
        }
    }
}
export default connect(null, mapDispatchToProps)(UserContainer)
