import Axios from 'axios';

export default class AuthService {
    registerUser(data){
        return Axios({
            url : 'http://svcy.myclass.vn/api/QuanLyTrungTam/DangKy',
            method : 'POST',
            data
        })
    };
    loginUser(TaiKhoan, MatKhau){
        return Axios({
            url : `http://svcy.myclass.vn/api/QuanLyTrungTam/DangNhap?taikhoan=${TaiKhoan}&matkhau=${this.state.MatKhau}`,
            method : 'GET'
        })
    }
};
