        <Route 
            {...rest} 
            render={ (routeProps) => {
                let data = sessionStorage.getItem('UserLogin');
                // if(data){
                //     var maLoaiNguoiDung = (JSON.parse(data)).MaLoaiNguoiDung;
                //     if(maLoaiNguoiDung === 'GV'){
                //         return <Component {...routeProps} />
                //     }
                //     if(maLoaiNguoiDung === 'HV'){
                //         return <Redirect to="/admin" />
                //     }
                // }
                // else {
                //     return <Redirect to="/" />;
                // }
                if(data){
                    return <Component {...routeProps} />
                }else{
                    alert()
                    return <Redirect to="/" />;
                }
                }}
        />
    );
};

export default Auth;
