import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actGetAllUsersRequest, actChangeEditStatus } from '../../store/action/user.action';
import UserItem from '../../components/Admin/UserItem';
import './User.css'
import { actSearchUser } from '../../store/action/search.action';

class UserContainer extends Component {
    componentDidMount() {
        this.props.getAllUser();
    }
    onChangeStatusEdit = () => {
        this.props.changeStatusEdit();
    }
    handleOnChange = (e) => {
        this.props.keyword(e.target.value.toLowerCase());
    }
    render() {
        var { searchUser, usersList } = this.props;
        var userElm = null;
        var displaySearchUser = [];
        displaySearchUser = usersList.filter(user => user.TaiKhoan.indexOf(searchUser) !== -1)
        userElm = displaySearchUser.map((user, index) => {
            return <UserItem
                key={index}
                index={index + 1}
                user={user}
            />
        })
        return (
            <div className="card shadow mb-4">
                <div className="card-header py-3 d-flex justify-content-between">
                    <h6 className="m-0 font-weight-bold text-primary">Manage Users</h6>
                    <button
                        className="btn btn-primary"
                        data-toggle="modal" data-target="#action_user"
                        onClick={this.onChangeStatusEdit}
                    >
                        Add User
                    </button>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <div className="row">
                            </div>
                    </div>
                </div>
            </div>

        )
    }
}
const mapStateToProps = state => {
    console.log(state);

    return {
        usersList: state.user,
        userEditing: state.userEditing,
        searchUser: state.searchUser
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getAllUser: () => {
            dispatch(actGetAllUsersRequest());
        },
        changeStatusEdit: () => {
            dispatch(actChangeEditStatus());
        },
        keyword: (keyword) => {
            dispatch(actSearchUser(keyword));
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
