import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actEditProfileRequest } from '../../../store/action/profile.acation.js';

class ProfileModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            TaiKhoan: '',
            MatKhau: '',
            HoTen: '',
            Email: '',
            SoDT: '',
            MaLoaiNguoiDung: 'HV'
        }
    }
    componentWillReceiveProps = nextProps => {
        // var { userProfileFromStore } = this.props;
        // console.log(nextProps);
        
        this.setState({
            TaiKhoan: nextProps.userProfileFromStore.TaiKhoan,
            // MatKhau: nextProps.userProfileFromStore.MatKhau,
            HoTen: nextProps.userProfileFromStore.HoTen,
            Email: nextProps.userProfileFromStore.Email,
            SoDT: nextProps.userProfileFromStore.SoDT,
            MaLoaiNguoiDung: nextProps.userProfileFromStore.MaLoaiNguoiDung
        })
    }
    onSubmit = e => {
        e.preventDefault();
        
        let nguoiDung = {
            TaiKhoan: 'nguyenhoanghuykma',
            MatKhau: 'slsdcdsl75',
            HoTen: 'Nguyen Hoang Huy',
            Email: 'huy@gmail.com',
            SoDT: '097844496',
            MaLoaiNguoiDung: 'HV'
        };
        this.props.editProfile(nguoiDung);
    }
    handleOnChange = e => {
        var { name, value } = e.target;
        this.setState({
            [name]: value
        },()=> {
            
        })

    }
    render() {
        var { userProfileFromStore } = this.props;
        var { TaiKhoan, HoTen, Email, SoDT, MaLoaiNguoiDung } = this.state


        return (
            <div className="modal fade" id="editprofile">
                <div className="modal-dialog  bg-modal ">
                    <div className="modal-content">
                        <form onSubmit={this.onSubmit}>

                            {/* Modal Header */}
                            <div className="modal-header">
                                <h4 className="modal-title" style={{ fontSize: '25px', fontWeight: '700' }}>Edit Profile</h4>
                                <button type="button" className="close" data-dismiss="modal">×</button>
                            </div>
                            {/* Modal body */}
                            <div className="modal-body">
                                <div className="form-group">
                                    <label>User</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={TaiKhoan}
                                        readOnly
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="email"
                                        className="form-control"
                                        value={Email}
                                        name="Email"
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Full name</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={HoTen}
                                        name="HoTen"
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >Phone</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={SoDT}
                                        name="SoDT"
                                        onChange={this.handleOnChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        value=""
                                        name="MatKhau"
                                        onChange={this.handleOnChange}
                                    />
                                </div>

                            </div>
                            {/* Modal footer */}
                            <div className="modal-footer">
                                <button type="submit" className="btn btn-primary">Change</button>
                                <button type="button" className="btn btn-danger" data-dismiss="modal">Close</button>
                            </div>
                        </form>

                    </div>

                </div>
            </div>
        )
    }
}
const mapStateToProps = state => {
    // console.log(state);

    return {
        userProfileFromStore: state.getProfileUser
    }
}
const mapDispatchToProps = (dispatch, props) => {
    return {
        editProfile: user => {
            console.log(user);
            dispatch(actEditProfileRequest(user));
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ProfileModal);
