import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';
import HomePage from '../pages/HomePage/HomePage';

const menus = [
    {
        name : 'Home',
        to : '/',
        exact : true
    },
    {
        name : 'Courses',
        to : '/courses',
        exact : false
    }
];
const MenuLink = ({ label, to, activeOnlyWhenExact}) => {
    return (
        <Route
            path={to}
            exact={activeOnlyWhenExact}
            children={ ({match}) => {
                let active = match ? 'active' : '';
                    
                <li className={active}>
                    <Link to={to}>
                        {label}
                    </Link>
                </li>
                
            }}
        />
    )
}
showMenu = (menus) => {
    var result =  n
}

class Menu extends Component {
    render() {
        return (
            <nav className="colorlib-nav" role="navigation">
                <div className="upper-menu">
                    <div className="container">
                        <div className="row">
                            <div className="col-xs-4">
                                <p>The ideal WordPress theme for online learning center</p>
                            </div>
                            <div className="col-xs-6 col-md-push-2 text-right">
                                <p className="btn-apply"><a href="#">Our Vision</a> <a href="#">Our Missions</a> <a href="#">Sign Up</a></p>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="top-menu">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-2">
                                <div id="colorlib-logo"><a href="index.html">Knowledge</a></div>
                            </div>
                            <div className="col-md-10 text-right menu-1">
                                <ul>
                                    {this.showMenu(menus)}
                                    <li className="active"><a href="index.html">Home</a></li>
                                    <li className="has-dropdown">
                                        <a href="courses.html">Courses</a>
                                        <ul className="dropdown">
                                            <li><a href="courses-single.html">Courses Single</a></li>
                                            <li><a href="#">Mobile Apps</a></li>
                                            <li><a href="#">Website</a></li>
                                            <li><a href="#">Web Design</a></li>
                                            <li><a href="#">WordPress</a></li>
                                        </ul>
                                    </li>
                                    <li><a href="teachers.html">Teachers</a></li>
                                    <li><a href="about.html">About</a></li>
                                    <li><a href="event.html">Events</a></li>
                                    <li><a href="contact.html">Contact</a></li>
                                    <li className="btn-cta"><a href="#"><span>Get started</span></a></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </nav>

        )
    }
}
export default Menu;
